export default createStore({
  state() {
    return {
      users: [] // Rename userList to users
    };
  },
  mutations: {
    setUsers(state, users) { // Rename setUserList to setUsers
      state.users = users;
    },
    // Remove filterUserList mutation as it's no longer needed
    updateUser(state, updatedUser) {
      const userIndex = state.users.findIndex(user => user.id === updatedUser.id);
      if (userIndex !== -1) {
        const updatedUserCopy = {
          ...state.users[userIndex],
          fullName: `${updatedUser.first_name} ${updatedUser.last_name}`,
          avatar: updatedUser.avatar
        };
        state.users.splice(userIndex, 1, updatedUserCopy);
      }
    },
    deleteUser(state, userId) {
      const userIndex = state.users.findIndex(user => user.id === userId);
      if (userIndex !== -1) {
        state.users.splice(userIndex, 1);
      }
    },
    addUser(state, newUser) {
      state.users.push(newUser);
    }
  },
  actions: {
    async fetchUserList({ commit }) {
      try {
        const response = await axios.get('https://reqres.in/api/users');
        const users = response.data.data.map(user => ({
          id: user.id,
          fullName: `${user.first_name} ${user.last_name}`,
          avatar: user.avatar
        }));
        commit('setUsers', users);
      } catch (error) {
        console.error('Failed to fetch user list:', error);
      }
    },
    // ...
  }
});
